mapper.select:
select  
b.id,b.config_id as configId,b.operatio_name as operatioName,b.arraignment_time as arraignmentTime,
b.arraignment_people as arraignmentPeople,b.arraignment_people_id as arraignmentPeopleId,
b.task_status as taskStatus,b.company_name as companyName,
b.credit_code as creditCode,b.version as version,b.type as type,b.revoke_cause as revokeCause,prseno
from sys_check_business b where 1=1

	and b.id = #{id,jdbcType=BIGINT}


and b.prseno = #{prseno,jdbcType=BIGINT}


	and b.operatio_name like concat('%',#{operatioName,jdbcType=VARCHAR},'%')


	and b.arraignment_people_id= #{arraignmentPeopleId,jdbcType=VARCHAR}


	and b.task_status= #{taskStatus,jdbcType=VARCHAR}


	and b.arraignment_time >= #{startArraignmentTime,jdbcType=VARCHAR}


	and b.arraignment_time <= #{endArraignmentTime,jdbcType=VARCHAR}  


	and b.arraignment_people like concat('%',#{arraignmentPeople,jdbcType=VARCHAR},'%')

order by b.create_time DESC

select count(*) from sys_check_business b where 1=1

		and b.id = #{id,jdbcType=BIGINT}


		and b.operatio_name like concat('%',#{operatioName,jdbcType=VARCHAR},'%')


		and b.arraignment_people_id= #{arraignmentPeopleId,jdbcType=VARCHAR}


		and b.task_status= #{taskStatus,jdbcType=VARCHAR}


		and b.arraignment_time >= #{startArraignmentTime,jdbcType=VARCHAR}


		and b.arraignment_time <= #{endArraignmentTime,jdbcType=VARCHAR}  


		and b.arraignment_people like concat('%',#{arraignmentPeople,jdbcType=VARCHAR},'%')
