mapper.select:
select i.*,p.step,p.`name` as proName,p.dispatch_rule as dispatchRule from sys_check_information i,sys_check_process p where p.id = i.pro_id and  i.check_people_id = #{checkPeopleId,jdbcType=VARCHAR}

	and i.check_result = #{checkResult,jdbcType=VARCHAR}

order by i.create_time DESC

select DISTINCT t.bus_id from sys_check_information t where t.check_people_id  = #{userId,jdbcType=BIGINT} and t.check_result = #{checkResult,jdbcType=VARCHAR}
and t.bus_id in (
	select ta.bus_id from sys_check_information ta where  ta.check_result=1 and ta.pro_id in (
	   select cp2.id from sys_check_process cp2 where 1=1 and (

			      (cp2.step= #{r.step} and cp2.config_id=#{r.configId} and cp2.version=#{r.version} )
		    )
	) 
)

select DISTINCT t.bus_id from sys_check_information t where t.check_people_id  = #{userId,jdbcType=BIGINT} and t.check_result = #{checkResult,jdbcType=VARCHAR}
and t.bus_id in (
	select ta.bus_id from sys_check_information ta where  ta.check_result=0 and ta.pro_id in (
	   select cp2.id from sys_check_process cp2 where 1=1 and (

			      (cp2.step= #{r.step} and cp2.config_id=#{r.configId} and cp2.version=#{r.version} )
		    )
	) 
)

select bus_id from sys_check_information where bus_id in

			#{list}

	and check_result=2
