mapper.select:

select

	${FIELDS}


	*

from sys_role


	 ORDER BY ${ORDERLIST}


	 ORDER BY create_time desc

select * from sys_role a


		a.role_name = #{roleName,jdbcType=VARCHAR}


		and a.id != #{roleId,jdbcType=BIGINT}


		and a.org_id = #{orgId,jdbcType=BIGINT}

select * from sys_role t where active='1'

 and t.role_type=#{type}

 and t.org_id in

	#{item}

select a.id as id,a.role_name as roleName,a.role_desc as roleDesc,a.role_type as roleType,d.org_name as orgName,a.position as position,c.title as title,c.fun_type as funType from sys_role a left outer join sys_role_function b on a.id=b.role_id left outer join sys_function c on b.function_id=c.id left outer join sys_org d on a.org_id=d.id where a.active=1

   and a.role_name like concat('%',#{roleName},'%')


	and a.role_desc like concat('%',#{roleDesc},'%')


	and a.org_id =#{orgId}


	and a.role_type =#{roleType}

select id,role_name as roleName from sys_role where org_id in (
select id from sys_org where parent_id in (select id from sys_org where parent_id=12) and orgenter_code is not null and orgenter_code<>'' and id<>1115)
and DATE_FORMAT(create_time,'%m-%d-%Y')='06-29-2020' and org_id<>1108 ORDER by org_id;

select a.id as id,a.role_name as roleName,a.role_desc as roleDesc,a.role_type as roleType,a.position as position from sys_role a
where a.role_code in

	#{roleCode}
